                var statisticI = statistics.Where(s => s.ReplacedBy == null &&
                                                       s != statisticO &&
                                                       s.TableName == statisticO.TableName &&
                                                       s.Columns.Count <= columns.Count &&
                                                       s.Columns.All(c => columns.ContainsKey(c)))
                                           .OrderBy(s => s.NewStats)
                                           .ThenByDescending(s => s.Columns.Count)
                                           .FirstOrDefault();
                if (statisticI == null)
                {
                    continue;
                }

                statisticI.ReplacedBy = statisticO;
                statisticO.Replaces = statisticI;
            }

